import React, { useState } from 'react';

const DienteSVG = ({ numero, tipo = 'molar' }) => {
  const [estadoCaras, setEstadoCaras] = useState({
    oclusal: 'sano',    // blanco
    vestibular: 'sano', // blanco
    lingual: 'sano',    // blanco
    mesial: 'sano',     // blanco
    distal: 'sano'      // blanco
  });

  const colores = {
    sano: '#ffffff',
    caries: '#ef4444',    // rojo
    tratado: '#3b82f6',   // azul
    obturado: '#10b981',  // verde
    extraido: '#6b7280'   // gris
  };

  const handleCaraClick = (cara) => {
    const estados = ['sano', 'caries', 'tratado', 'obturado', 'extraido'];
    const estadoActual = estadoCaras[cara];
    const indiceActual = estados.indexOf(estadoActual);
    const siguienteEstado = estados[(indiceActual + 1) % estados.length];
    
    const nuevoEstado = {
      ...estadoCaras,
      [cara]: siguienteEstado
    };
    
    setEstadoCaras(nuevoEstado);
    
    // Console.log para futura integración con base de datos
    console.log(`Diente ${numero} - Cara ${cara}: ${siguienteEstado}`, nuevoEstado);
  };

  const getSVGPaths = () => {
    switch (tipo) {
      case 'incisor':
        return {
          oclusal: "M10 2 L14 2 L14 4 L10 4 Z",
          vestibular: "M10 2 L14 2 L14 10 L10 10 Z",
          lingual: "M10 2 L14 2 L14 10 L10 10 Z",
          mesial: "M10 2 L10 10 L8 10 L8 2 Z",
          distal: "M14 2 L14 10 L16 10 L16 2 Z"
        };
      case 'canino':
        return {
          oclusal: "M9 2 L15 2 L15 4 L9 4 Z",
          vestibular: "M9 2 L15 2 L15 10 L9 10 Z",
          lingual: "M9 2 L15 2 L15 10 L9 10 Z",
          mesial: "M9 2 L9 10 L7 10 L7 2 Z",
          distal: "M15 2 L15 10 L17 10 L17 2 Z"
        };
      case 'premolar':
        return {
          oclusal: "M8 2 L16 2 L16 4 L8 4 Z",
          vestibular: "M8 2 L16 2 L16 10 L8 10 Z",
          lingual: "M8 2 L16 2 L16 10 L8 10 Z",
          mesial: "M8 2 L8 10 L6 10 L6 2 Z",
          distal: "M16 2 L16 10 L18 10 L18 2 Z"
        };
      case 'molar':
      default:
        return {
          oclusal: "M7 2 L17 2 L17 4 L7 4 Z",
          vestibular: "M7 2 L17 2 L17 10 L7 10 Z",
          lingual: "M7 2 L17 2 L17 10 L7 10 Z",
          mesial: "M7 2 L7 10 L5 10 L5 2 Z",
          distal: "M17 2 L17 10 L19 10 L19 2 Z"
        };
    }
  };

  const paths = getSVGPaths();

  return (
    <div className="flex flex-col items-center">
      <svg width="28" height="36" viewBox="0 0 28 36" className="cursor-pointer">
        {/* Oclusal/Incisal */}
        <path
          d={paths.oclusal}
          fill={colores[estadoCaras.oclusal]}
          stroke="#000000"
          strokeWidth="0.5"
          onClick={() => handleCaraClick('oclusal')}
          className="hover:opacity-80"
        />
        
        {/* Vestibular */}
        <path
          d={paths.vestibular}
          fill={colores[estadoCaras.vestibular]}
          stroke="#000000"
          strokeWidth="0.5"
          onClick={() => handleCaraClick('vestibular')}
          className="hover:opacity-80"
        />
        
        {/* Lingual */}
        <path
          d={paths.lingual}
          fill={colores[estadoCaras.lingual]}
          stroke="#000000"
          strokeWidth="0.5"
          onClick={() => handleCaraClick('lingual')}
          className="hover:opacity-80"
        />
        
        {/* Mesial */}
        <path
          d={paths.mesial}
          fill={colores[estadoCaras.mesial]}
          stroke="#000000"
          strokeWidth="0.5"
          onClick={() => handleCaraClick('mesial')}
          className="hover:opacity-80"
        />
        
        {/* Distal */}
        <path
          d={paths.distal}
          fill={colores[estadoCaras.distal]}
          stroke="#000000"
          strokeWidth="0.5"
          onClick={() => handleCaraClick('distal')}
          className="hover:opacity-80"
        />
      </svg>
      <span className="text-xs font-medium text-gray-700 mt-1">{numero}</span>
    </div>
  );
};

export default DienteSVG;
