                        {/* Dientes superiores 21-28 */}
                        {[21, 22, 23, 24, 25, 26, 27, 28].map((numero, index) => (
                          <PeriodontoTooth
                            key={numero}
                            x={560 + index * 48}
                            y={50}
                            type={index >= 6 ? 'molar' : index >= 4 ? 'premolar' : index === 3 ? 'canino' : 'incisor'}
