      <NewPatientModal
        isOpen={isNewPatientOpen}
        onClose={() => setIsNewPatientOpen(false)}
        onCreate={() => setIsNewPatientOpen(false)}
      />

      {/* Modal: Perfil de Paciente */}
      {isPatientProfileOpen && selectedPatient && (
        <div className="fixed inset-0 z-[100]">
