                <button className="p-2 text-gray-400 hover:text-gray-600">
                  <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 4a1 1 0 011-1h16a1 1 0 011 1v2.586a1 1 0 01-.293.707l-6.414 6.414a1 1 0 00-.293.707V17l-4 4v-6.586a1 1 0 00-.293-.707L3.293 7.293A1 1 0 013 6.586V4z"/>
                  </svg>
                </button>
              </div>
            </div>
            {notasEvolucion.length === 0 ? (
              <div className="text-center py-8">
                <p className="text-gray-500">No hay notas de evoluciÃ³n</p>
              </div>
            ) : (
              <div className="space-y-4 max-h-64 overflow-y-auto">
                {notasEvolucion.map((nota) => (
                  <div key={nota.id} className="border border-gray-200 rounded-lg p-4">
                    <div className="flex items-center justify-between mb-2">
                      <span className="text-sm font-medium text-gray-900">{nota.doctor}</span>
                      <span className="text-xs text-gray-500">
                        {new Date(nota.fecha).toLocaleDateString('es-ES')}
                      </span>
                    </div>
                    {nota.evolucion && (
                      <div className="mb-2">
                        <p className="text-xs text-gray-600 font-medium mb-1">EvoluciÃ³n:</p>
                        <p className="text-sm text-gray-800">{nota.evolucion}</p>
                      </div>
                    )}
                    {nota.observacion && (
                      <div>
                        <p className="text-xs text-gray-600 font-medium mb-1">ObservaciÃ³n:</p>
                        <p className="text-sm text-gray-800">{nota.observacion}</p>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Presupuestos */}
          <div className="p-6">
            <div className="text-center">
              <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <svg className="w-8 h-8 text-blue-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"/>
                </svg>
              </div>
              <p className="text-gray-600 text-sm">Este paciente aÃºn no tiene presupuestos</p>
            </div>
          </div>
        </div>
      </div>

      {/* BotÃ³n de cerrar */}
      <button
        onClick={onClose}
